import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router'
import { compose } from 'redux'

import { getCategories, getActiveCategory } from 'selectors'

const Categories = ({ categories, activeCategory }) => {
  const renderCategory = category => {
    let classes = 'categories__list_item'
    classes = (activeCategory === category.id) ? `${classes} active` : classes

    return (
      <Link
        key={category.id}
        to={`/categories/${category.id}`}
        className={classes}
      >
        {category.name}
      </Link>
    )
  }

  const renderAllCategories = () => {
    let classes = 'categories__list_item'
    classes = (activeCategory === undefined) ? `${classes} active` : classes

    return (
      < Link to='/' className={classes}>
        All
      </Link >
    )
  }

  return (
    <div className='categories'>
      <h4>Brand</h4>
      <div className="categories__list">
        {renderAllCategories()}
        {categories.map(category => renderCategory(category))}
      </div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => {
  return {
    categories: getCategories(state),
    activeCategory: getActiveCategory(ownProps)
  }
}

export default compose(
  withRouter,
  connect(mapStateToProps)
)(Categories)