import React from 'react'
import { connect } from 'react-redux'

import { getPhonesBasketWithCount, getTotalBasketPrice } from 'selectors'

const Basket = ({ phones, totalBasketPrice }) => {
  const renderContent = () => {
    return (
      <div className="basket__table table">
        {(phones.length === 0) && <div>Your shopping cart is empty.</div>}
        <div className='table__xxx'>
          <table className="table__responsive">
            <tbody>
              {phones.map((phone, index) => (
                <tr key={index}>
                  <td>
                    <div className="table__image">
                      <img src={phone.image} alt={phone.name} />
                    </div>
                  </td>
                  <td>{phone.name}</td>
                  <td>${phone.price}</td>
                  <td>{phone.count}</td>
                  <td><i className="fas fa-trash-alt"></i></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {(phones.length > 0) && (
          <div className='basket__total-sum'>
            <b>Total: </b>
            ${totalBasketPrice}
          </div>
        )}
      </div>
    )
  }

  const renderSidebar = () => {
    return (
      <div>Sidebar</div>
    )
  }

  return (
    <div className='basket'>
      <div className="basket__content">
        {renderContent()}
      </div>
      <div className="basket__sidebar">
        {renderSidebar()}
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    phones: getPhonesBasketWithCount(state),
    totalBasketPrice: getTotalBasketPrice(state)
  }
}

export default connect(mapStateToProps)(Basket)