import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

import { fetchPhoneById, addPhoneToBasket } from 'actions'
import { getPhoneById } from 'selectors'
import BasketCart from 'components/basketCart'

class Phone extends Component {
  componentDidMount() {
    this.props.fetchPhoneById(this.props.match.params.id)
  }

  renderFields(phone) {
    const columnFields = Object.keys(phone).filter(key => (
      key === 'cpu' ||
      key === 'size' ||
      key === 'weight' ||
      key === 'display' ||
      key === 'memory' ||
      key === 'battery'
    ))

    return columnFields.map((column, index) => (
      <div className='cart__fields_item' key={index}>
        <h4>{column}</h4>
        <span>{phone[column]}</span>
      </div>
    ))

  }

  renderContent(phone) {
    return (
      <div className='cart'>
        <div className="cart__info">
          <div className="cart__image">
            <img src={phone.image} alt={phone.name} />
          </div>
          <div className="cart__fields">
            {this.renderFields(phone)}
          </div>
        </div>
        <div className="cart__title">
          <h4>{phone.name}</h4>
          <h4>${phone.price}</h4>
        </div>
        <div className="cart__text">
          {phone.description}
        </div>
      </div>
    )
  }

  renderSidebar(phone) {
    const { addPhoneToBasket } = this.props
    
    return (
      <div>
        <h3>Quick shop</h3>
        <BasketCart />
        <h1>{phone.name}</h1>
        <h2>${phone.price}</h2>
        <Link
          to='/'
          className="button button-block"
        >
          Back to store
        </Link>
        <button
          className="button button-add button-block"
          onClick={() => addPhoneToBasket(phone.id)}
        >
          Add to cart
        </button>
      </div>
    )
  }

  render() {
    const { phone } = this.props

    return (
      <div className='phone-page'>
        <div className="phone-page__content">
          {phone && this.renderContent(phone)}
        </div>
        <div className="phone-page__sidebar">
          {phone && this.renderSidebar(phone)}
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  }
}

const mapDispatchToProps = {
  fetchPhoneById,
  getPhoneById,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Phone)