import React, { useState } from 'react'
import { FormControlLabel, FormGroup, Switch } from '@material-ui/core'
import { withRouter } from 'react-router-dom'

const ToggleSwitch = ({ history }) => {
  const [switched, setSwitched] = useState(false)

  const toggleChecked = event => {
    if (!switched) {
      history.push('/sign-in')
      
    } else {
      history.push('/')
    }

    setSwitched(event.target.checked)
  }

  return (
    <FormGroup className='toggle-switch'>
      <FormControlLabel
        control={<Switch checked={switched} onChange={toggleChecked} />}
        label={switched ? 'Logout' : ` Login`}
      // style={switched ? {color: 'red'} : {color: 'white'}}
      // labelPlacement='end'
      />
    </FormGroup>
  )
}

export default withRouter(ToggleSwitch)