import React, { Fragment } from 'react'
import Button from '@material-ui/core/Button'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import { Link } from "react-router-dom";

const ScrollDialog = ({
  label, open, scroll, to,
  handleClickOpen, handleClose,
  className
}) => {
  return (
    <Fragment>
      <Link to={to} onClick={handleClickOpen} className={className}>{label}</Link>
      <Dialog
        open={open}
        onClose={handleClose}
        scroll={scroll}
        aria-labelledby='scroll-dialog-title'
        aria-describedby='scroll-dialog-description'
        style={{minWidth: 320}}
      >
        <DialogTitle id='scroll-dialog-title'>Privacy Policy</DialogTitle>
        <DialogContent dividers={scroll === 'paper'}>
          <DialogContentText
            id='scroll-dialog-description'
          >
            {[...new Array(50)]
              .map(() => `Cras mattis consectetur purus sit amet fermentum.
Cras justo odio, dapibus ac facilisis in, egestas eget quam.
Morbi leo risus, porta ac consectetur ac, vestibulum at eros.
Praesent commodo cursus magna, vel scelerisque nisl consectetur et.`)
              .join('\n')}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color='primary'>
            Agree
        </Button>
        </DialogActions>
      </Dialog>
    </Fragment>
  )
}

export default ScrollDialog