import '../auth.sass'

import React, { useState } from 'react'
import { Paper, TextField, Typography, Button } from '@material-ui/core'
import VpnKeyIcon from '@material-ui/icons/VpnKey'
import MailOutline from '@material-ui/icons/MailOutline'
import LockOpenIcon from '@material-ui/icons/LockOpen'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { validateControl } from 'internetShop/selectors'
import ScrollDialog from 'Mui-3/auth/ScrollDialog'
import { auth } from 'internetShop/actions'

const SignIn = ({ auth }) => {
  const [formControls, setFormControls] = useState({
    email: {
      type: 'email',
      label: 'Email',
      value: '',
      valid: false,
      touched: false,
      errorMessage: 'Incorrect email.',
      validation: {
        required: true,
        email: true,
      },
    },
    password: {
      type: 'password',
      label: 'Password',
      value: '',
      valid: false,
      touched: false,
      errorMessage: 'At least 6 characters.',
      validation: {
        required: true,
        minLength: 6
      },
    },
  })
  const [isFormValid, setIsFormValid] = useState(true)
  const [open, setOpen] = useState(false)

  const header = (
    <div className="auth__header">
      <Typography variant='h4' color='primary'>
        Sign In
      </Typography>
      <Typography variant='h4' color='secondary'>
        Sign Up
      </Typography>
    </div>
  )

  const handleChange = (e, controlName) => {
    const newFormControls = { ...formControls }
    const control = newFormControls[controlName]

    control.value = e.target.value
    control.touched = true
    control.valid = validateControl(control.value, control.validation)

    let isFormValid = true

    Object.keys(newFormControls).map(name => (
      isFormValid = newFormControls[name].valid && isFormValid
    ))

    setFormControls(newFormControls)
    setIsFormValid(isFormValid)
  }

  const renderFormControls = () => {
    return Object.keys(formControls).map((controlName, index) => {
      const control = formControls[controlName]

      let iconField = null
      switch (controlName) {
        case 'email':
          iconField = <MailOutline fontSize='small' />
          break
        case 'password':
          iconField = <VpnKeyIcon fontSize='small' />
          break
        case 'verifyPassword':
          iconField = <LockOpenIcon fontSize='small' />
          break
        default:
          iconField = null
      }

      const isInvalid = !control.valid && control.touched
      const errorMessage = isInvalid
        ? control.errorMessage || 'Incorrect entry'
        : ''

      return (
        <div className='auth__field' key={index}>
          {iconField}
          <TextField
            id={`${control.label}`}
            type={control.type}
            label={control.label}
            value={control.value}
            error={isInvalid}
            helperText={errorMessage}
            onChange={(e) => handleChange(e, controlName)}
          />
        </div>
      )
    })
  }

  const handleClickAuth = () => auth(
    formControls.email.value,
    formControls.password.value,
    true
  )

  const handleOpenDialog = () => setOpen(true)
  const handleCloseDialog = () => setOpen(false)

  return (
    <form className='auth'>
      <Paper elevation={3} className='auth__sign-in'>

        {header}

        {renderFormControls()}

        <Link to='/recovery' className="auth__forgotPass">
          Fogot Password?
        </Link>

        <Button
          variant='contained'
          color='primary'
          onClick={handleClickAuth}
          disabled={!isFormValid}
        >
          Sign In
        </Button>

        <ScrollDialog
          to='sign-in'
          label='Privacy Policy'
          className="auth__policy"
          handleOpenDialog={handleOpenDialog}
          handleCloseDialog={handleCloseDialog}
          open={open}
        />

        <Link to='/' className="auth__cancel">
          Cancel
        </Link>

      </Paper>
    </form>
  )
}

const mapStateToProps = state => {
  return {
    error: state.internetShop.auth.error
  }
}

const mapDispatchToProps = {
  auth
}

export default connect(mapStateToProps, mapDispatchToProps)(SignIn)