import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox'
import MailIcon from '@material-ui/icons/Mail'
import SmartphoneIcon from '@material-ui/icons/Smartphone'
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket'
import { Link } from 'react-router-dom'

import './style.sass'

const useStyles = makeStyles({
  list: { 
    width: 250, 
  },
  link: {
    ':hover': {
      textDecoration: 'none',
    },
  },
});

const TemporaryDrawer = ({ closeDrawer, isOpen }) => {
  const classes = useStyles();

  const links = [
    { to: '/', label: 'Phones', icon: <SmartphoneIcon style={{ color: 'orange' }} /> },
    { to: 'basket', label: 'Basket', icon: <ShoppingBasketIcon style={{ color: 'green' }} /> },
    { to: 'sign-up', label: 'Sign Up', icon: <i className='fas fa-user-plus' style={{ color: 'blue' }}></i> }
  ]

  const sideList = () => (
    <div
      className={classes.list}
      role="presentation"
    >
      <List>
        {links.map(link => (
          <Link to={link.to} key={link.label} className={classes.link}>
            <ListItem button >
              <ListItemIcon style={{ minWidth: 40 }}>{link.icon}</ListItemIcon>
              <ListItemText primary={link.label} />
            </ListItem>
          </Link>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon style={{ minWidth: 40 }}>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
  );

  return (
    <Drawer open={isOpen} onClick={closeDrawer}>
      {sideList('left')}
    </Drawer>
  );
}

export default TemporaryDrawer