import React, { useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
// import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import { Link } from 'react-router-dom'

import TemporaryDrawer from 'Mui/Top-2/Drawer';
import useStyles from './styles'
import HideOnScroll from 'Mui/Top-2/HideOnScroll';
import LoginSwitch from 'Mui/Top-2/LoginSwitch';
// import { StylesProvider } from '@material-ui/core';

import './style.sass'

export default function ButtonAppBar(props) {
  const classes = useStyles();
  const [state, setState] = useState({
    openMenu: false
  })

  const toggleDrawer = () => {
    setState({ ...state, openMenu: !state.openMenu });
  };

  const closeDrawer = () => setState({ ...state, openMenu: false })

  return (
    <div className={classes.root}>
      <HideOnScroll {...props}>
        <AppBar /* position="static" */>
          <Toolbar>

            <IconButton
              edge="start"
              className={classes.menuButton}
              color="inherit"
              aria-label="menu"
              onClick={toggleDrawer}
            >
              <MenuIcon />
            </IconButton>

            <TemporaryDrawer closeDrawer={closeDrawer} isOpen={state.openMenu} />

            <Typography variant="h6" className={classes.title}>
              Quick Shop
            </Typography>

            <LoginSwitch />

            {/* <StylesProvider injectFirst> */}
              <Link to='/' className='sign-up'>Sign Up</Link>
            {/* </StylesProvider> */}

          </Toolbar>
        </AppBar>
      </HideOnScroll>
    </div>
  );
}