import React from 'react'
import { SwipeableDrawer, List, ListItem, ListItemIcon, ListItemText } from '@material-ui/core';
import { Link } from 'react-router-dom';
import SmartphoneIcon from '@material-ui/icons/Smartphone';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
// import { StylesProvider} from '@material-ui/styles';

import useStyles from '../style';
import '../style.sass';

const SwipeableTemporaryDrawer = ({ openDrawer, closeDrawer, toggleDrawer }) => {
  console.log("TCL: SwipeableTemporaryDrawer -> toggleDrawer", toggleDrawer)
  const classes = useStyles()
  const links = [
    { to: '/', label: 'Phones', icon: <SmartphoneIcon style={{ color: 'orange' }} /> },
    { to: '/basket', label: 'Basket', icon: <ShoppingBasketIcon style={{ color: 'green' }} /> },
    { to: '/sign-up', label: 'Sign Up', icon: <i className='fas fa-user-plus' style={{ color: 'blue' }}></i> },
  ]

  const list = () => {
    return (
      <div
        className={classes.list}
        onClick={closeDrawer}
      >
        <List>
          {links.map(link => (
            <Link to={link.to} key={link.label} className='link'/* {classes.link} */>
              <ListItem button>
                <ListItemIcon>
                  {link.icon}
                </ListItemIcon>
                <ListItemText primary={link.label} />
              </ListItem>
            </Link>
          ))}
        </List>
      </div>
    )
  }

  return (
    // <StylesProvider injectFirst>
      <div className='drawer'>
        <SwipeableDrawer
          open={openDrawer}
          onClose={closeDrawer}
          onOpen={toggleDrawer}
        >
          {list()}
        </SwipeableDrawer>
      </div>
    // </StylesProvider> 
  )
}

export default SwipeableTemporaryDrawer