import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

import { fetchPhoneById, addPhoneToBasket } from 'internetShop/actions'
import { getPhoneById } from 'internetShop/selectors'
import BasketCart from 'internetShop/components/BasketCart'

const Phone = ({ 
  fetchPhoneById, 
  match, 
  phone,
  addPhoneToBasket
}) => {
  
  useEffect(() => {
    fetchPhoneById(match.params.id)
  }, [fetchPhoneById, match.params.id])

  const renderFields = phone => {
    const fields = Object.keys(phone).filter(key => (
      key === 'cpu' ||
      key === 'display' ||
      key === 'size' ||
      key === 'weight' ||
      key === 'memory' ||
      key === 'battery'
    ))

    return fields.map((field, index) => (
      <div className='card__fields_item' key={index}>
        <h5>{field}</h5>
        <span>{phone[field]}</span>
      </div>
    ))
  }

  const renderContent = phone => {
    return (
      <div className='card'>
        <div className="card__info">
          <div className="card__image">
            <img src={phone.image} alt={phone.name} />
          </div>
          <div className="card__fields">
            {renderFields(phone)}
          </div>
        </div>
        <div className="card__title">
          <h4>{phone.name}</h4>
          <h4>${phone.price}</h4>
        </div>
        <div className="card__text">
          {phone.description}
        </div>
      </div>
    )
  }

  const renderSidebar = phone => {
    return (
      <div>
        <h5>Quick Shop</h5>
        <BasketCart />
        <h1>{phone.name}</h1>
        <h2>${phone.price}</h2>
        <Link
          to='/'
          className='button button-block button-inverse'
        >
          Back to store
        </Link>
        <button
          className="button button-block button-success"
          onClick={() => addPhoneToBasket(phone.id)}
        >
          Add to cart
        </button>
      </div>
    )
  }

  return (
    <div className='phone'>
      <div className="phone__content">
        {phone && renderContent(phone)}
      </div>
      <div className="phone__sidebar">
        {phone && renderSidebar(phone)}
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.internetShop.phonePage.id)
  }
}

const mapDispatchToProps = {
  fetchPhoneById,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Phone)