import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

import { 
  fetchPhones, 
  loadMorePhones,
  addPhoneToBasket,
  fetchCategories
} from 'internetShop/actions'

import { getPhones } from 'internetShop/selectors'
import Layout from 'internetShop/containers/Layout'
// import { sendPhonesToServer, sendCategoriesToServer } from 'internetShop/api'

const Phones = ({
  fetchPhones,
  fetchCategories,
  phones,
  loadMorePhones,
  // addPhoneToBasket
}) => {
  
  useEffect(() => {
    // sendPhonesToServer()
    // sendCategoriesToServer()
    fetchPhones()
    fetchCategories()
  }, [fetchPhones, fetchCategories])

  const renderPhone = (phone, index) => {
    const shortDescription = `${phone.description.slice(0, 61)}...`

    return (
      <div className='phones__item' key={index}>
        <div className="card">
          <div className="card__image">
            <img src={phone.image} alt={phone.name} />
          </div>
          <div className="card__title">
            <h5>
              <Link
                to={`/phones/${phone.id}`}
              >
                {phone.name}
              </Link>
            </h5>
            <span>${phone.price}</span>
          </div>
          <div className="card__text">
            {shortDescription}
          </div>
          <div className="card__buttons">
            <button
              className="button"
              onClick={() => addPhoneToBasket(phone.id)}
            >
              Buy now!
            </button>
            <Link
              to={`/phones/${phone.id}`}
              className="button button-info"
            >
              More info
            </Link>
          </div>
        </div>
      </div>
    )
  }

  return (
    <Layout>
      <div className='phones'>
        {phones.map((phone, index) => renderPhone(phone, index))}
      </div>
      <div className="phones__more">
        <button
          className="button"
          onClick={loadMorePhones}
        >Load More</button>
      </div>
    </Layout>
  )
}

const mapStateToProps = (state, ownProps) => {
  return {
    phones: getPhones(state, ownProps)
  }
}

const mapDispatchToProps = {
  fetchPhones,
  fetchCategories,
  loadMorePhones,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Phones)