const initialState = {
  token: null,
  error: undefined
};

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case 'AUTH_SUCCESS':
      return {...state, token: payload, error: undefined}

    case 'AUTH_FEILURE':
      return {...state, error: payload}

    case 'AUTH_LOGOUT':
      return {...state, token: null}

    default:
      return state
  }
};