import React from 'react'

const ItemStatusFilter = ({ onFilterTask, filterTask }) => {
  const buttons = [
    { label: 'All', name: 'all' },
    { label: 'Active', name: 'active' },
    { label: 'Done', name: 'done' }
  ]

  const renderButton = (button, index) => {
    const classes = button.name === filterTask
      ? `btn btn-primary`
      : `btn btn-outline-secondary`

    return (
      <button
        key={index}
        className={classes}
        onClick={() => onFilterTask(button.name)}
      >
        {button.label}
      </button>
    )
  }

  return (
    <div className='btn-group'>
      {buttons.map((button, inedx) => renderButton(button, inedx))}
    </div>
  )
}

export default ItemStatusFilter