import React from 'react'
import { Link, withRouter } from 'react-router-dom'

import Backdrop from 'topLine/components/Backdrop'
import PropTypes from 'prop-types'

const Drawer = ({
  location,
  onToggleDrawer,
  openMenu,
  onToggleBackdrop
}) => {
  const activeLink = location.pathname
  const links = [
    { to: '/', label: 'Phones' },
    { to: '/basket', label: 'Basket' },
    { to: '/authorization', label: 'Authorization' }
  ]

  const classDrawer = openMenu
    ? 'drawer'
    : 'drawer close-drawer'

  const renderLink = (link, index) => {
    const classes = link.to === activeLink
      ? `drawer__link active`
      : 'drawer__link'

    return (
      <Link
        key={index}
        to={link.to}
        className={classes}
        onClick={onToggleDrawer}
      >
        {link.label}
      </Link>
    )
  }

  return (
    <div>
      <div className={classDrawer}>
        {links.map((link, index) => renderLink(link, index))}
      </div>
      {openMenu && <Backdrop onToggleBackdrop={onToggleBackdrop} />}
    </div>
  )
}

Drawer.propTypes = {
  location: PropTypes.string,
  onToggleDrawer: PropTypes.func,
  openMenu: PropTypes.bool,
  onToggleBackdrop: PropTypes.func,
}

export default withRouter(Drawer)