import React, { useState } from 'react'

import ToggleMenu from 'topLine/components/ToggleMenu'
import Drawer from 'topLine/components/Drawer'

const TopLine = () => {
  const [openMenu, setOpenMenu] = useState(false)

  const onToggleMenu = () => setOpenMenu(!openMenu)

  const onToggleDrawer = () => setOpenMenu(false)

  const onToggleBackdrop = () =>setOpenMenu(false)

  return (
    <div className='top-line'>
      <ToggleMenu
        onToggleMenu={onToggleMenu}
        openMenu={openMenu}
      />
      <Drawer
        onToggleDrawer={onToggleDrawer}
        onToggleBackdrop={onToggleBackdrop}
        openMenu={openMenu}
      />
    </div>
  )
}

export default TopLine