import React from 'react'
import Drawer from '@material-ui/core/Drawer'
import ListItem from '@material-ui/core/ListItem'
import { Link } from 'react-router-dom'
import SmartphoneIcon from '@material-ui/icons/Smartphone'
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket'

import classesDrawer from './styles'

export default ({ toggleDrawer, open }) => {
  const links = [
    { to: '/', label: 'Phones', icon: <SmartphoneIcon /> },
    { to: '/basket', label: 'Basket', icon: <ShoppingBasketIcon /> },
    { to: '/sign-up', label: 'Sign Up', icon: <i className='fas fa-user-plus'></i> }
  ]

  const classes = classesDrawer()

  const list = () => (
    <div
      className={classes.root}
      role={'presentation'}
      onClick={toggleDrawer}
    >
      {links.map((link, index) => (
        <ListItem button key={index}>
          {link.icon}
          <Link to={link.to} className={classes.link} >
            {link.label}
          </Link>
        </ListItem>
      ))}
    </div>
  )

  return (
    <Drawer open={open} onClick={toggleDrawer}>
      {list()}
    </Drawer>
  )
}