import React, { useEffect } from 'react'
import { Grid, Typography, Card, CardContent, Button } from '@material-ui/core';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import useStyles from './styles';
import { fetchPhoneById, addPhoneToBasket } from '../../store/actions/phonesActions';
import { getPhoneById } from 'selectors';
import BasketCart from 'components/BasketCart';

const Phone = ({ fetchPhoneById, match, phone, addPhoneToBasket }) => {
  const classes = useStyles();

  useEffect(() => {
    fetchPhoneById(match.params.id);
  });

  const renderFields = () => {
    const fields = Object.keys(phone).filter(key => (
      key === 'cpu' ||
      key === 'size' ||
      key === 'weight' ||
      key === 'display' ||
      key === 'memory' ||
      key === 'battery'
    ));

    return (
      fields.map(field => (
        <div className={classes.field} key={field}>
          <Typography variant="h6">
            {field}:
          </Typography>
          <Typography variant="body1">
            {phone[field]}
          </Typography>
        </div>
      ))
    );
  };

  const renderPhone = () => {
    return (
      <Card raised /* variant="outlined" */>
        <CardContent>
          <Grid container className={classes.info}>
            <Grid container item md={6}>
              {/* <CardMedia
                component="img"
                src={phone.image}
                alt={phone.name}
                className={classes.image}
              /> */}
              <img src={phone.image} alt={phone.name} className={classes.image} />
            </Grid>

            <Grid container item md={6}>
              {renderFields()}
            </Grid>
          </Grid>

          <div className={classes.title}>
            <Typography gutterBottom variant="h4">
              {phone.name}
            </Typography>
            <span>${phone.price}</span>
          </div>

          <Typography variant="body1">
            {phone.description}
          </Typography>
        </CardContent>
      </Card>
    );
  };

  const handleAddPhoneToBasket = id => addPhoneToBasket(id);

  const renderSidebar = () => (
    <div>
      <Typography gutterBottom variant="h5">
        Quick Shop
      </Typography>

      <BasketCart />

      <Typography gutterBottom variant="h4" className={classes.titlePhone} >
        {phone.name}
      </Typography>
      <Typography gutterBottom variant="h4">
        ${phone.price}
      </Typography>

      <Link to="/" className={classes.link} >
        <Button className={classes.backToStore} fullWidth>
          Back to store
        </Button>
      </Link>

      <Button className={classes.addPhoneToBasket} fullWidth onClick={() => handleAddPhoneToBasket(phone.id)}>
        Add to cart
      </Button>
    </div>
  );

  return (
    <div className={classes.root}>
      <Grid container spacing={5}>
        <Grid item xs={12} md={9}>
          {phone && renderPhone()}
        </Grid>

        <Grid item xs={12} md={3}>
          {phone && renderSidebar()}
        </Grid>
      </Grid>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  };
};

const mapDispatchToProps = {
  fetchPhoneById,
  addPhoneToBasket
};

export default connect(mapStateToProps, mapDispatchToProps)(Phone);