import React, { useEffect } from 'react'
import { Grid, Typography, Card, CardContent, CardMedia } from '@material-ui/core';
import { connect } from 'react-redux';

import useStyles from './styles';
import { fetchPhoneById } from '../../store/actions/phonesActions';
import { getPhoneById } from 'selectors';

const Phone = ({ fetchPhoneById, match, phone }) => {
  console.log("Phone -> phone", phone)
  // console.log("Phone -> match", match.params.id)
  const classes = useStyles();

  useEffect(() => {
    fetchPhoneById(match.params.id);
  });

  const renderPhone = () => {
    return (
      <Card className={classes.ok} variant="outlined">
        <CardContent>
          <Grid container>
            <Grid container item md={6}>
              <CardMedia
                component="img"
                src={phone.image}
                alt={phone.name}
                className={classes.image}
              />
              {/* <img src={phone.image} alt={phone.name} /> */}
            </Grid>

            <Grid container item md={6}>
              Content
          </Grid>
          </Grid>

          <div className={classes.title}>
            <Typography gutterBottom variant="h4">
              {phone.name}
            </Typography>
            <span>${phone.price}</span>
          </div>

          <Typography variant="body1">
            {phone.description}
          </Typography>
        </CardContent>
      </Card>
    );
  };

  return (
    <div className={classes.root}>
      <Grid container spacing={5}>
        <Grid container item md={9}>
          {phone && renderPhone()}
        </Grid>

        <Grid container item md={3}>
          Sidebar
        </Grid>
      </Grid>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  };
};

const mapDispatchToProps = {
  fetchPhoneById
};

export default connect(mapStateToProps, mapDispatchToProps)(Phone);