import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { fetchPhones } from 'store/actions/phonesActions';

import './phones.sass';
import PhoneCard from 'components/PhoneCard';

const Phones = ({ fetchPhones, phones }) => {
  useEffect(() => {
    fetchPhones()
  });

  const renderPhone = (phone, index) => {
    // console.log("TCL: renderPhone -> phone", phone);
    if (index > 1) return;
    const shortDescription = `${phone.description.slice(0, 61)}...`;

    return (
      <div key={index} className="phones__card">
        <div className="card">
          <div className="card__image">
            <img src={phone.image} alt={phone.name} />
          </div>
          <div className="card__title">
            <Link to={`/phones/${phone.id}`}>
              <h4>{phone.name}</h4>
            </Link>
              <h4>${phone.price}</h4>
          </div>
          <div className="card__description">
            {shortDescription}
          </div>
          <div className="card__buttons">
            <button className="button">Buy now!</button>
            <Link to={`/phones/${phone.id}`} className="button button_info">
              More info
          </Link>
          </div>
        </div>

        <PhoneCard phone={phone} />
      </div>
    )
  }

  return (
    <div className="phones">
      <div className="phones__sidebar">
        Sidebar
      </div>
      <div className="phones__content">
        {phones.map((phone, index) => renderPhone(phone, index))}
      </div>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    phones: state.phones
  }
};

const mapDispatchToProps = {
  fetchPhones
};

export default connect(mapStateToProps, mapDispatchToProps)(Phones)