import React from 'react';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import SmartphoneTwoToneIcon from '@material-ui/icons/SmartphoneTwoTone';
import ShoppingBasketTwoToneIcon from '@material-ui/icons/ShoppingBasketTwoTone';
import PersonAddTwoToneIcon from '@material-ui/icons/PersonAddTwoTone';
import ExitToAppTwoToneIcon from '@material-ui/icons/ExitToAppTwoTone';
import PlaylistAddCheckTwoToneIcon from '@material-ui/icons/PlaylistAddCheckTwoTone';
import MeetingRoomTwoToneIcon from '@material-ui/icons/MeetingRoomTwoTone';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import useStyles from '../topLineStyles';

const TopLineSwipeableDrawer = ({ open, toggleDrawer, isAuthenticated }) => {
  const classes = useStyles();
  const links = isAuthenticated
    ? [
      { to: '/', label: 'Phones', icon: <SmartphoneTwoToneIcon style={{ color: 'orange' }} /> },
      { to: '/basket', label: 'Basket', icon: <ShoppingBasketTwoToneIcon style={{ color: 'green' }} /> },
      { to: '/task-list', label: 'Task List', icon: <PlaylistAddCheckTwoToneIcon style={{ color: '#63bdf1' }} /> },
      { to: '/logout', label: 'Logout', icon: <MeetingRoomTwoToneIcon style={{ color: '#63bdf1' }} /> }
    ]
    : [
      { to: '/', label: 'Phones', icon: <SmartphoneTwoToneIcon style={{ color: 'orange' }} /> },
      { to: '/basket', label: 'Basket', icon: <ShoppingBasketTwoToneIcon style={{ color: 'green' }} /> },
      { to: '/sign-in', label: 'Sign In', icon: <ExitToAppTwoToneIcon style={{ color: '#63bdf1' }} /> },
      { to: '/sign-up', label: 'Sign Up', icon: <PersonAddTwoToneIcon style={{ color: '#63bdf1' }} /> }
    ];

  const list = () => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        {links.map(link => (
          <Link to={link.to} className={classes.link} key={link.label}>
            <ListItem button>
              <ListItemIcon className={classes.listIcon}>{link.icon}</ListItemIcon>
              <ListItemText primary={link.label} className={classes.listText} />
            </ListItem>
          </Link>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon className={classes.listIcon}>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
  );

  return (
    <div>
      <Drawer open={open} onClose={toggleDrawer(false)}>
        {list()}
      </Drawer>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    isAuthenticated: Boolean(state.auth.token)
  };
};

export default connect(mapStateToProps)(TopLineSwipeableDrawer)