import React, { useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { withRouter } from 'react-router-dom';
import { Link } from 'react-router-dom';

import TopLineSwipeableDrawer from 'containers/TopLine/TopLineDrawer';
import useStyles from './topLineStyles';
import ToggleSwitch from 'containers/TopLine/ToggleSwitch';
import { Menu, MenuItem } from '@material-ui/core';
// import renderMobileMenu from 'containers/TopLine/TopLineSwipeableDrawer/MobileMenu';

const TopLine = ({ history }) => {
  const classes = useStyles();

  const [openDrawer, setOpenDrawer] = useState(false);
  const [signIn, setSignIn] = useState(false);
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);

  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const toggleDrawer = open => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setOpenDrawer(open);
  };

  const handleChange = event => {
    if (signIn) {
      history.push('/');

    } else {
      history.push('/sign-in');
    }
    setSignIn(event.target.checked)
  };

  const handleMobileMenuOpen = event => setMobileMoreAnchorEl(event.currentTarget);
  const handleMobileMenuClose = () => setMobileMoreAnchorEl(null);

  const mobileMenuId = "menu-mobile";
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      className={classes.menuItem}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <MenuItem>
        <Link
          to="/sign-in"
          className={classes.signInMobileMenu}
          onClick={handleMobileMenuClose}
        >Sign In</Link>
      </MenuItem>
      <MenuItem>
        <Link
          to="/sign-up"
          className={classes.signUpMobileMenu}
          onClick={handleMobileMenuClose}
        >Sign Up</Link>
      </MenuItem>
    </Menu>
  );

  return (
    <div className={classes.root}>
      <AppBar /* position="static" */ color='primary'>
        <Toolbar>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="inherit"
            aria-label="menu"
            onClick={toggleDrawer(true)}
          >
            <MenuIcon />
          </IconButton>

          <Typography variant="h6" className={classes.title}>
            Quick Shop
          </Typography>

          <div className={classes.sectionDesctop}>
            <TopLineSwipeableDrawer open={openDrawer} toggleDrawer={toggleDrawer} />

            <ToggleSwitch signIn={signIn} handleChange={handleChange} className={classes.signIn} />

            <Link to="/sign-up" className={classes.signUp}>Sign Up</Link>
          </div>

          <div className={classes.sectionMobile}>
            <IconButton
              aria-label="show-more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MoreVertIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
    </div >
  );
}

export default withRouter(TopLine)