import { combineReducers } from 'redux';

import auth from 'store/reducers/auth';
import phones from 'store/reducers/phones';
import phonesPage from 'store/reducers/phonesPage';
import phonePage from 'store/reducers/phonePage';
import basket from 'store/reducers/basket';
import categories from 'store/reducers/categories';

export default combineReducers({
  auth,
  phones,
  phonesPage,
  phonePage,
  basket,
  categories
});